sourcesContent:
import mongoose from 'mongoose';

const { Schema, model } = mongoose;

const productSchema = new Schema({
  title: {
    type: String,
    required: true,
  },
  price: {
    type: Number,
    required: true,
  },
  stock: {
    type: Number,
    default: 0,
  },
  description: String,
  image: String,
  category: String,
});

const Product = model('Product', productSchema);

export default Product;

